import React from 'react';
import { connect } from 'react-redux'
import { changeTimer, inputTaskName } from '../store/actions'
import { Timer } from './Timer';
import { TaskNameInput } from './TaskNameInput';
import { StartStopButton } from './StartStopButton';
import { ScoreTable } from './ScoreTable';
// import { withUser } from './withUser'
// import { compose } from 'redux'

class MainComponent extends React.Component{
    render() {
        const { isTimerWorking,
                startTime,
                startMoment,
                taskNameInput,
                doneTasks,
                changeTimer,
                inputTaskName
        } = this.props
        return (
        //     <div id='main'>
        //         <div id='wrapper'>
        //             <button id='minus' onClick={decreaseCount}>-</button>
        //             <input value={count} disabled />
        //             <button id='plus' onClick={increaseCount}>+</button><br/>
        // <button id='reset' onClick={resetCount}>{this.props.name}</button>
        //         </div>
        //     </div>
            <div>
                <Timer />
                <TaskNameInput 
                    data={taskNameInput}
                    edit={e => inputTaskName(e.target.value)}
                />
                <StartStopButton action={changeTimer} />
                <ScoreTable data={doneTasks} />
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        isTimerWorking: state.isTimerWorking,
        startTime: state.startTime,
        startMoment: state.startMoment,
        taskNameInput: state.taskNameInput,
        doneTasks: state.doneTasks
    }
}

const mapDispatchToProps = { changeTimer, inputTaskName }

export default connect(mapStateToProps, mapDispatchToProps)(MainComponent)
