import React from 'react'

export const Timer = props => {
    const { 
        work, 
        minutes, 
        seconds, 
        plusMinute, 
        plusSecond, 
        restart 
    } = props

    let timeOut
    if(work){
        timeOut = setTimeout(() => plusSecond(), 1000)
        if(seconds > 59){
            plusMinute()
            clearTimeout(timeOut)
        }
    } else if(!work){
        restart()
        clearTimeout(timeOut)
    }

    return(
        <div>       
            {(minutes < 10) ? `0${minutes}` : minutes}:{(seconds < 10) ? `0${seconds}` : seconds}
        </div>
    )
}
