import React from 'react'
import { connect } from 'react-redux'

export const Timer = ({ isTimerWorking, seconds, changeSecondsCount, resetCount }) => {
    if(isTimerWorking){
        // setTimeout(() => {
        //     changeSecondsCount()
        // }, 1)
        setTimeout(() => changeSecondsCount(), 1000)
    } else if(seconds){
        setImmediate(() => resetCount())
    }

    console.log(seconds)

    const showMinutes = parseInt(parseInt(seconds)/60)
    const showSeconds = parseInt(seconds) % 60

    return(
        <div id="clock">       
            {(showMinutes < 10) ? `0${showMinutes}` : showMinutes}:{(showSeconds < 10) ? `0${showSeconds}` : showSeconds}
        </div>
    )
}

const mapStateToProps = state => {
    return {
        isTimerWorking: state.isTimerWorking,
        attentionBanner: state.attentionBanner,
        taskNameInput: state.taskNameInput,
        doneTasks: state.doneTasks,
        seconds: state.seconds,
    }
}

const mapDispatchToProps = { 
    changeTimer, 
    inputTaskName, 
    changeSecondsCount,
    resetCount
}

export default connect(mapStateToProps, mapDispatchToProps)(MainComponent)
