import React from 'react'

export const Timer = ({ work, minutes, seconds, plusMinutes, plusSeconds, min }) => {
    // const timeOut = () => {
    //     setTimeout(() => plusSeconds(), 1000)
    // }

    let timeOut

    if(work){
        timeOut = setTimeout(() => plusSeconds(), 1000)
    } else clearTimeout(timeOut)


    return(
        <div>       
            {(minutes < 10) ? `0${minutes}` : minutes}:{(seconds < 10) ? `0${seconds}` : seconds}
        </div>
    )
}
