import React from 'react'
import { connect } from 'react-redux'
import { increaseMinutesCount, increaseSecondsCount } from '../store/actions'

class Timer extends React.Component{
    shouldComponentUpdate(){
        const { 
            isTimerWorking,
            minutes,
            seconds,
            increaseMinutesCount,
            increaseSecondsCount
        } = this.props

        console.log()

        if(isTimerWorking){
            this.interval = setInterval(() => increaseSecondsCount(), 1000)
        }

        // if(seconds > 5){
        //     increaseMinutesCount()
        // } else {
        //     this.interval = setInterval(() => increaseSecondsCount(), 1000)
        // }
    }

    componentDidUpdate() {
        clearInterval(this.interval);
    }

    render(){
        const {
            minutes,
            seconds
        } = this.props
        return(
            <div>       
                {(minutes < 10) ? `0${minutes}` : minutes}:{(seconds < 10) ? `0${seconds}` : seconds}
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        isTimerWorking: state.isTimerWorking,
        minutes: state.time.minutes,
        seconds: state.time.seconds
    }
}

const mapDispatchToProps = { increaseMinutesCount, increaseSecondsCount }

export default connect(mapStateToProps, mapDispatchToProps)(Timer)
