import React from 'react';
import { connect } from 'react-redux'
import { changeTimer, inputTaskName, changeSecondsCount, resetCount } from '../store/actions'
import { Timer } from './Timer';
import { TaskNameInput } from './TaskNameInput';
import { StartStopButton } from './StartStopButton';
import { ScoreTable } from './ScoreTable';

class MainComponent extends React.Component{
    render(){
        const { 
            isTimerWorking,
            seconds,
            taskNameInput,
            doneTasks,
            changeTimer,
            inputTaskName,
            changeSecondsCount,
            resetCount
        } = this.props

        return(
            <div id="wrapper">
                <div id="main">
                <Timer 
                    work={isTimerWorking} 
                    seconds={seconds}
                    plusSecond={changeSecondsCount}
                    restart={resetCount}
                />
                <TaskNameInput 
                    data={taskNameInput}
                    edit={e => inputTaskName(e.target.value)}
                    work={isTimerWorking}
                    action={changeTimer}
                />
                <StartStopButton action={changeTimer} />
                </div>
                <ScoreTable data={doneTasks} />
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        isTimerWorking: state.isTimerWorking,
        taskNameInput: state.taskNameInput,
        doneTasks: state.doneTasks,
        seconds: state.seconds,
    }
}

const mapDispatchToProps = { 
    changeTimer, 
    inputTaskName, 
    changeSecondsCount,
    resetCount
}

export default connect(mapStateToProps, mapDispatchToProps)(MainComponent)
