import React from 'react'
import TextField from '@material-ui/core/TextField'

export const TaskNameInput = ({ data, edit, work, action, 
click }) => {
    let isAccess
    let btnClick

    if(click){
        btnClick = 'inputTaskFalse'
    } else {
        btnClick = 'inputTaskTrue'
    }



    if(work){
        isAccess = true
    } else {
        isAccess = false
    }

    return(
        <div className={btnClick}>
            <form onSubmit={action}>
                <TextField 
                    id="standard-basic" 
                    label="Enter task name"
                    value={data}
                    onChange={edit}
                    disabled={isAccess}
                />
            </form>
        </div>
    )
}
