import React from 'react'

export const Timer = props => {
    const { 
        work, minutes, seconds, plusMinute, plusSecond, minusSeconds, restart } = props


    // const timeOut = () => {
    //     setTimeout(() => plusSeconds(), 1000)
    // }

    let timeOut

    if(work){
        timeOut = setTimeout(() => plusSecond(), 1000)
    } else restart()


    return(
        <div>       
            {(minutes < 10) ? `0${minutes}` : minutes}:{(seconds < 10) ? `0${seconds}` : seconds}
        </div>
    )
}
