import React from 'react';
import { connect } from 'react-redux'
import { changeTimer, inputTaskName, increaseMinutesCount, increaseSecondsCount } from '../store/actions'
import { Timer } from './Timer';
import { TaskNameInput } from './TaskNameInput';
import { StartStopButton } from './StartStopButton';
import { ScoreTable } from './ScoreTable';
// import { withUser } from './withUser'
// import { compose } from 'redux'

class MainComponent extends React.Component{
    render(){
        const { 
            isTimerWorking,
            minutes,
            seconds,
            startTime,
            startMoment,
            taskNameInput,
            doneTasks,
            changeTimer,
            inputTaskName,
            increaseMinutesCount,
            increaseSecondsCount
        } = this.props

        return(
            <div>
                <Timer 
                    work={isTimerWorking} 
                    minutes={minutes}
                      />
                <TaskNameInput 
                    data={taskNameInput}
                    edit={e => inputTaskName(e.target.value)}
                />
                <StartStopButton action={changeTimer} />
                <ScoreTable data={doneTasks} />
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        isTimerWorking: state.isTimerWorking,
        startTime: state.startTime,
        startMoment: state.startMoment,
        taskNameInput: state.taskNameInput,
        doneTasks: state.doneTasks,
        isTimerWorking: state.isTimerWorking,
        minutes: state.time.minutes,
        seconds: state.time.seconds
    }
}

const mapDispatchToProps = { changeTimer, inputTaskName }

export default connect(mapStateToProps, mapDispatchToProps, increaseMinutesCount, increaseSecondsCount)(MainComponent)
