import React from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

class ScoreItem extends React.Component{
    render() {
        const id = this.props.match.params.id
        const {doneTasks} = this.props
        const arr = doneTasks.filter(elem => `id${elem.id}` === id)
        const elem = arr[0]

        return(
            <table>
                <tr>
                    <th>Id</th>
                    <th>Task name</th>
                    <th>Start time</th>
                    <th>Stop time</th>
                    <th>Duration</th>
                </tr>
                <tr>
                    <td>{elem.id}</td>
                    <td>{elem.name}</td>
                    <td>{elem.startTime.hours}:{elem.startTime.minutes}</td>
                    <td>{elem.stopTime.hours}:{elem.stopTime.minutes}</td>
                    <td>{elem.duration}s</td>
                </tr>
            </table>
        )
    }
}

const mapStateToProps = state => {
    return {
        doneTasks: state.doneTasks
    }
}

export default connect(mapStateToProps)(ScoreItem)




