import { CHANGE_TIMER } from './actions.js'
import { GET_TASK_NAME } from './actions.js'
import { CHANGE_SECONDS } from './actions.js'
import { RESET_COUNT } from './actions.js'

let initialState

if(localStorage['redux-store']){
    initialState = JSON.parse(localStorage['redux-store'])
} else {
    initialState = {
        isTimerWorking: false,
        onButtonClick: false,
        seconds: 0,
        startTime: {
            hours: null,
            minutes: null,
        },
        startMoment: null,
        taskNameInput: '',
        doneTasks: []
    }
}

export const reducer = (state = initialState, action) => {
    if (!state.isTimerWorking && state.taskNameInput && action.type === CHANGE_TIMER){
        return {...state, isTimerWorking: true,
            onButtonClick: false,
            startTime: {
                hours: new Date().getHours(),
                minutes: new Date().getMinutes(),
            },
            startMoment: Date.now() 
        }
    } else if (!state.isTimerWorking && !state.taskNameInput && action.type === CHANGE_TIMER){
        return {
            ...state, onButtonClick: true
        }
    } else if (state.isTimerWorking && action.type === CHANGE_TIMER){
        return {...state, isTimerWorking: false,
            doneTasks: [...state.doneTasks, {
                id: Date.now(),
                name: state.taskNameInput,
                startTime: state.startTime,
                stopTime: {
                    hours: new Date().getHours(),
                    minutes: new Date().getMinutes(),
                },
                duration: ((Date.now() - state.startMoment)/1000).toFixed(1)
            }],
            startTime: {
                hours: null,
                minutes: null,
            },
            startMoment: null,
            taskNameInput: ''
        }
    } else if (action.type === GET_TASK_NAME){
        return {...state, taskNameInput: action.payload}
    } else if (action.type === CHANGE_SECONDS){
        return {...state, seconds: parseInt((action.payload - state.startMoment)/1000)}
    } else if (action.type === RESET_COUNT){
        return {...state, seconds: 0}
    } else return state
}
