import React from 'react'

export const TaskNameInput = ({ data, edit, work }) => {
    let isAccess
    let inputPlaceholder
    if(work){
        isAccess = true
        inputPlaceholder = 'Task in progress...'
    } else {
        isAccess = false
        inputPlaceholder = 'Enter task name'
    }

    return(
        <div><input 
            placeholder={inputPlaceholder}
            value={data}
            onChange={edit}
            disabled={isAccess}
        /></div>
    )
}
